import React from "react";

import Movie from "./components/Movie.js";

function App () {
  const movies = ["1","2","3"]; //array of 3 movies
// this part loops over the movies
  return <div>
    {movies.map(movie => (
      <Movie />
    ))}
  </div>;
}

export default App;
