import React, { useEffect, useState } from "react";

import Movie from "./components/Movie";

const FEATURED_API = "https://api.themoviedb.org/3/movie/popular?api_key=291df565a5587bfbe331b3122abe6384";
//const IMG_API = "http://image.tmdb.org/t/p/w300";
const SEARCH_API = 
"https://api.themoviedb.org/3/search/movie?&api_key=291df565a5587bfbe331b3122abe6384&query=";

function App () {
  const [movies, setMovies] = useState ([]);

  useEffect(() => {
    fetch(FEATURED_API)
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      setMovies(data.results);
    });
  }, []);

const handleOnSubmit = (e) => {
  e.preventDefault();
};

  return (
    <>
    <header>
      <form onSubmit={handleOnSubmit}>
        <input
          className="search" 
          type="search"
          placeholder="Search..." 

      />
      </form>
      </header>
      <div className="movie-container">
        {movies.length > 0 && 
    movies.map((movie) => <Movie key={movie.id} {...movie} />)}
    </div>
    </>
    );
}

export default App;
